import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import HeadingSection from "../HeadingSection/HeadingSection";
import TestimonialItem from "./TestimonialItem";
import testimonialsData from "../../data/Testimonials/testimonials-data.json";

const TestimonialsRestaurant = ({ title, tagline, font }) => {
  const settings = {
    dots: true,
    centerMode: true,
    centerPadding: "0",
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    className: "slick testimonial",
  };
  return (
    <section className="parallax-bg-9">
      <div className="parallax-overlay"></div>
      <div className="container">
        <div className="row">
          <HeadingSection
            title={title}
            tagline={tagline}
            classAppend="white-color"
            font={font}
          />
        </div>
        <div className="row">
          <div className="col-md-12">
            <Slider {...settings}>
              {testimonialsData.map((testimonial) => (
                <TestimonialItem
                  key={testimonial.id}
                  avatar={testimonial.avatar}
                  name={testimonial.name}
                  role={testimonial.role}
                >
                  {testimonial.text}
                </TestimonialItem>
              ))}
            </Slider>
          </div>
        </div>
      </div>
    </section>
  );
};

export default TestimonialsRestaurant;
